mapper.select:
SELECT id, name, username, email, address, phone, website, company
FROM User
WHERE id = #{id}

mapper.update:
UPDATE User
SET name = #{name}, username = #{username}, password = #{password}, email = #{email}, address = #{address}, phone = #{phone}, website = #{website}, company = #{company}
WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM User
WHERE username = #{username}

    AND id != #{id}

SELECT count(*)
FROM User
WHERE email = #{email}

    AND id != #{id}
